var connection = require('./test/common.js').createConnection();

var sql = "insert into insert_test values (?, ?);";

connection.query(sql, [55558, 'qqq'], function(err, rows, f) {
  // rows.affectedRows DOES exist
  console.log(rows, f);
  console.log(rows.affectedRows);
});

// But with...

connection.execute(sql, [55559, 'www'], function(err, rows, f) {
  // rows.affectedRows does NOT exist
  console.log('execute:', rows, f);
  console.log('execute', rows.affectedRows);
});
